import React, {useState, useEffect} from "react";
import "./Chat.scss";
import send from "../../images/send.svg";
import { formatRelative } from 'date-fns';

export const Chat = ({messageDB}) => {

    const [messages, setMessages] = useState([]);

    useEffect(() => {
        if (messageDB) {
            messageDB.collection('messages').orderBy('date').onSnapshot(querySnapshot => {
                const data = querySnapshot.docs.map(doc => ({
                    ...doc.data(),
                    id: doc.id,
                }))
                setMessages(data);
            })
        }
    }, [messageDB]);

    return (
        <>
            <div className="chat">
                <div className="chat__users">
                    <img src="man_photo1.jpeg"/>
                    <p>Dawid Sowi≈Ñski</p>
                </div>
                <div className="messages">
                    {
                        messages.map(message => {
                            return (
                                <>
                                    <div className="messages__left">
                                        <div className="message">
                                            <p className="main__message">{message.text}</p>
                                            <p>{formatRelative(new Date(message.date.seconds * 1000), new Date())}</p>
                                        </div>
                                        <img src="man_photo2.jpeg"/>
                                    </div>
                                </>
                            )
                        })
                    }
                </div>
                <div className="send__messages">
                    <input type="text" placeholder="Type Your message..."/>
                    <a href=""><img src={send}/></a>
                </div>
            </div>
        </>
    )
}
